import React, { useEffect, useState } from "react";
import axios from "axios";

function PatientDashboard() {
    const [healthData, setHealthData] = useState([]);
    const [notifications, setNotifications] = useState([]);
    const [patientProfile, setPatientProfile] = useState({
        name: "John Doe",
        email: "johndoe@example.com",
        birthDate: "1990-01-01",
    });

    const patientId = "123"; // À remplacer par l'ID réel du patient connecté

    useEffect(() => {
        // Récupérer les données de santé
        axios.get(`http://localhost:8080/api/health-data/${patientId}`)
            .then(response => {
                setHealthData(response.data);
            })
            .catch(error => {
                console.error("Erreur lors de la récupération des données de santé :", error);
            });

        // Récupérer les notifications (exemple fictif, ajoutez l'endpoint correspondant)
        axios.get(`http://localhost:8080/api/notifications/${patientId}`)
            .then(response => {
                setNotifications(response.data);
            })
            .catch(error => {
                console.error("Erreur lors de la récupération des notifications :", error);
            });
    }, [patientId]);

    return (
        <div style={styles.dashboard}>
            {/* Section Profil */}
            <div style={styles.card}>
                <h2>Profil</h2>
                <p><strong>Nom :</strong> {patientProfile.name}</p>
                <p><strong>Email :</strong> {patientProfile.email}</p>
                <p><strong>Date de naissance :</strong> {new Date(patientProfile.birthDate).toLocaleDateString()}</p>
            </div>

            {/* Section États de santé */}
            <div style={styles.section}>
                <h2>États de santé</h2>
                {healthData.length > 0 ? (
                    healthData.map((data, index) => (
                        <div key={index} style={styles.card}>
                            <p><strong>Type :</strong> {data.type}</p>
                            <p><strong>Valeur :</strong> {data.value}</p>
                            <p><strong>Date :</strong> {new Date(data.timestamp).toLocaleString()}</p>
                        </div>
                    ))
                ) : (
                    <p>Aucune donnée de santé disponible.</p>
                )}
            </div>

            {/* Section Notifications */}
            <div style={styles.section}>
                <h2>Notifications</h2>
                {notifications.length > 0 ? (
                    notifications.map((notification, index) => (
                        <div key={index} style={styles.card}>
                            <p><strong>Message :</strong> {notification.message}</p>
                            <p><strong>Date :</strong> {new Date(notification.timestamp).toLocaleString()}</p>
                        </div>
                    ))
                ) : (
                    <p>Aucune notification disponible.</p>
                )}
            </div>
        </div>
    );
}

const styles = {
    dashboard: {
        padding: "20px",
        backgroundColor: "#f9f9f9",
    },
    section: {
        marginBottom: "30px",
    },
    card: {
        backgroundColor: "#fff",
        borderRadius: "10px",
        padding: "20px",
        marginBottom: "10px",
        boxShadow: "0 4px 10px rgba(0, 0, 0, 0.1)",
    },
};

export default PatientDashboard;
